mapper.select:
SELECT

FROM order_refund
WHERE id = #{id, jdbcType=VARCHAR}

SELECT

FROM order_refund
WHERE refund_sn = #{refundSn, jdbcType=VARCHAR}

SELECT

FROM order_refund


    AND refund_sn LIKE CONCAT('%', #{refundSn, jdbcType=VARCHAR}, '%')


    AND order_id = #{orderId, jdbcType=VARCHAR}


    AND user_id = #{userId, jdbcType=VARCHAR}


    AND refund_type = #{refundType, jdbcType=TINYINT}


    AND refund_reason LIKE CONCAT('%', #{refundReason, jdbcType=VARCHAR}, '%')


    AND apply_amount >= #{minApplyAmount, jdbcType=DECIMAL}


    AND apply_amount <= #{maxApplyAmount, jdbcType=DECIMAL}


    AND actual_amount >= #{minActualAmount, jdbcType=DECIMAL}


    AND actual_amount <= #{maxActualAmount, jdbcType=DECIMAL}


    AND refund_status = #{refundStatus, jdbcType=TINYINT}


    AND approver_id = #{approverId, jdbcType=VARCHAR}


    AND return_logistics = #{returnLogistics, jdbcType=VARCHAR}


    AND return_tracking_no = #{returnTrackingNo, jdbcType=VARCHAR}


    AND apply_time >= #{applyTimeStart, jdbcType=TIMESTAMP}


    AND apply_time <= #{applyTimeEnd, jdbcType=TIMESTAMP}


    AND review_time >= #{reviewTimeStart, jdbcType=TIMESTAMP}


    AND review_time <= #{reviewTimeEnd, jdbcType=TIMESTAMP}


    AND refund_time >= #{refundTimeStart, jdbcType=TIMESTAMP}


    AND refund_time <= #{refundTimeEnd, jdbcType=TIMESTAMP}


ORDER BY apply_time DESC

mapper.update:
UPDATE order_refund
SET refund_status = #{refundStatus, jdbcType=TINYINT}
WHERE id = #{id, jdbcType=VARCHAR}

UPDATE order_refund
SET approver_id = #{approverId, jdbcType=VARCHAR},
    approver_notes = #{approverNotes, jdbcType=VARCHAR},
    refund_status = #{refundStatus, jdbcType=TINYINT},
    review_time = #{reviewTime, jdbcType=TIMESTAMP}
WHERE id = #{id, jdbcType=VARCHAR}

UPDATE order_refund
SET actual_amount = #{actualAmount, jdbcType=DECIMAL},
    refund_status = #{refundStatus, jdbcType=TINYINT},
    refund_time = #{refundTime, jdbcType=TIMESTAMP}
WHERE id = #{id, jdbcType=VARCHAR}

UPDATE order_refund
SET return_logistics = #{returnLogistics, jdbcType=VARCHAR},
    return_tracking_no = #{returnTrackingNo, jdbcType=VARCHAR}
WHERE id = #{id, jdbcType=VARCHAR}
