INSERT INTO `product_categorytag` VALUES ('A0001', 'jewelry_type', 'Jewelry Type', '首饰类型', 'أنواع المجوهرات', 'Classification based on the form and wearing position of jewelry, such as necklaces, rings, bracelets, etc.', '根据首饰的形态和佩戴部位进行的分类，如项链、戒指、手链等。', 'التصنيف based on الشكل وموضع الارتداء for المجوهرات، مثل القلائد، الخواتم، الأساور، إلخ.', 'Jewelry form classification', '首饰形态分类', 'تصنيف شكل المجوهرات', '386780209580994560', '/api/files/PRODUCTEMO/2025/12/03/20251203152627989452.png', '/api/files/PRODUCTEMO/2025/12/03/20251203152628058043.jpg', '386780209866207232', '/api/files/PRODUCTEMO/2025/12/03/20251203152628062455.png', '386780209882984448', 1, '0', '#FF6B6B', 100, 1, 1, 1, 'Jewelry Types - Necklaces, Rings, Bracelets', '首饰类型 - 项链、戒指、手链', 'أنواع المجوهرات - قلائد، خواتم، أساور', 'Browse jewelry by type: necklaces for elegance, rings for commitment, bracelets for connection.', '按类型浏览珠宝：项链彰显优雅，戒指代表承诺，手链象征连接。', 'تصفح المجوهرات by النوع: قلائد for الأناقة، خواتم for الالتزام، أساور for الاتصال.', NULL, NULL, NULL, 1, 1, '2025-11-18 23:29:45', '2025-12-03 15:26:28');
INSERT INTO `product_categorytag` VALUES ('A00010001', 'necklace', 'Necklace', '项链', 'قلادة', 'Jewelry worn around the neck, ranging from simple chains to elaborate pendants. Symbolizes connection, elegance, and protection.', '佩戴于颈部的饰品，从简约链条款式到华丽吊坠设计。象征连接、优雅与守护。', 'مجوهرات تُلبس حول العنق، تتراوح من السلاسل البسيطة إلى الأpendants المتقنة. ترمز إلى الاتصال، الأناقة، والحماية.', 'Worn around neck', '佩戴于颈部', 'تُلبس حول العنق', '386780209580994560', '/api/files/PRODUCTEMO/2025/12/03/20251203152627989452.png', '/api/files/PRODUCTEMO/2025/12/03/20251203152628058043.jpg', '386780209866207232', '/api/files/PRODUCTEMO/2025/12/03/20251203152628062455.png', '386780209882984448', 1, 'A0001', '#FF6B6B', 100, 1, 1, 1, 'Necklace Collection - Elegant Neck Jewelry', '项链系列 - 优雅颈间饰品', 'مجموعة القلائد - مجوهرات عنقية أنيقة', 'Discover our exquisite necklace collection featuring various styles and materials for every occasion.', '探索我们的精美项链系列，包含多种风格和材质，适合各种场合佩戴。', 'اكتشف مجموعة قلائدنا الرائعة التي تضم أنماطًا وموادًا متناسبة لكل مناسبة.', NULL, NULL, NULL, 1, 1, '2025-11-18 23:36:47', '2025-12-03 15:46:22');
