INSERT INTO `product_categorytag` VALUES ('A00040002', 'vintage_classic', 'Vintage Classic', '复古经典', 'كلاسيكي عتيق', 'Timeless designs inspired by historical eras, featuring intricate details, traditional craftsmanship, and nostalgic elegance.', '受历史时期启发的永恒设计，具有复杂细节、传统工艺和怀旧优雅。', 'تصميمات خالدة مستوحاة من العصور التاريخية، تتميز بتفاصيل معقدة، حرفة تقليدية، وأناقة حنينية.', 'Timeless historical elegance', '永恒的历史优雅', 'أناقة تاريخية خالدة', '386780852488105984', '/api/files/PRODUCTEMO/2025/12/03/20251203152901270033.jpg', '/api/files/PRODUCTEMO/2025/12/03/20251203152901274346.png', '386780852496494592', '/api/files/PRODUCTEMO/2025/12/03/20251203152901276657.png', '386780852509077504', 4, 'A0004', '#8B4513', 200, 1, 1, 1, 'Vintage Classic Jewelry - Timeless Elegance', '复古经典珠宝 - 永恒优雅', 'مجوهرات كلاسيكية عتيقة - أناقة خالدة', 'Elegant vintage classic jewelry with historical inspiration and timeless appeal for special occasions', '优雅的复古经典珠宝，具有历史灵感和永恒魅力，适合特殊场合', 'مجوهرات كلاسيكية عتيقة أنيقة بإلهام تاريخي وجاذبية خالدة للمناسبات الخاصة', 'vintage jewelry, classic, antique, historical, traditional, heirloom', '复古珠宝, 经典, 古董, 历史, 传统, 传家宝', 'مجوهرات عتيقة, كلاسيكي, قديم, تاريخي, تقليدي, تراثي', 1, 1, '2025-12-03 14:52:13', '2025-12-03 15:58:19');
INSERT INTO `product_categorytag` VALUES ('A00040003', 'chinese_traditional', 'Chinese Traditional', '中式传统', 'تقليدي صيني', 'Jewelry incorporating Chinese cultural elements, symbolic motifs, auspicious patterns, and traditional craftsmanship techniques.', '融合中国文化元素、象征图案、吉祥纹样和传统工艺技术的珠宝。', 'مجوهرات تضم عناصر ثقافية صينية، زخارف رمزية، أنماط ميمونة، وتقنيات حرفية تقليدية.', 'Cultural heritage and symbolism', '文化遗产与象征意义', 'تراث ثقافي ورمزية', '386780852488105984', '/api/files/PRODUCTEMO/2025/12/03/20251203152901270033.jpg', '/api/files/PRODUCTEMO/2025/12/03/20251203152901274346.png', '386780852496494592', '/api/files/PRODUCTEMO/2025/12/03/20251203152901276657.png', '386780852509077504', 4, 'A0004', '#B22222', 300, 1, 1, 1, 'Chinese Traditional Jewelry - Cultural Heritage', '中式传统珠宝 - 文化遗产', 'مجوهرات تقليدية صينية - تراث ثقافي', 'Authentic Chinese traditional jewelry featuring cultural symbolism and auspicious designs for special celebrations', '正宗的中式传统珠宝，具有文化象征和吉祥设计，适合特殊庆典', 'مجوهرات تقليدية صينية أصلية تتميز برمزية ثقافية وتصاميم ميمونة للاحتفالات الخاصة', 'chinese jewelry, traditional, cultural, symbolic, auspicious, heritage', '中式珠宝, 传统, 文化, 象征, 吉祥, 遗产', 'مجوهرات صينية, تقليدي, ثقافي, رمزي, ميمون, تراث', 1, 1, '2025-12-03 14:52:13', '2025-12-03 15:58:19');
