INSERT INTO `product_categorytag` VALUES ('A00050007', 'casual', 'Casual', '休闲场合', 'غير رسمي', 'Relaxed and comfortable jewelry for casual outings, weekend activities, and informal gatherings, featuring easy-to-wear designs.', '用于休闲外出、周末活动和非正式聚会的轻松舒适珠宝，具有易于佩戴的设计。', 'مجوهرات مريحة ومرتاحة for الخروجات غير الرسمية، أنشطة نهاية الأسبوع، والتجمعات غير form، what تتميز بتصاميم سهلة الارتداء.', 'Relaxed casual jewelry', '轻松休闲珠宝', 'مجوهرات غير رسمية مرتاحة', '386780956460707840', '/api/files/PRODUCTEMO/2025/12/03/20251203152926061359.png', '/api/files/PRODUCTEMO/2025/12/03/20251203152926063349.jpg', '386780956473290752', '/api/files/PRODUCTEMO/2025/12/03/20251203152926066809.png', '386780956490067968', 5, 'A0005', '#4CAF50', 700, 1, 1, 1, 'Casual Jewelry - Relaxed Comfortable Pieces', '休闲珠宝 - 轻松舒适款式', 'مجوهرات غير رسمية - قطع مريحة مرتاحة', 'Comfortable and stylish jewelry perfect for casual weekends, brunch dates, shopping trips, and relaxed social occasions.', '舒适时尚的珠宝，完美适合休闲周末、早午餐约会、购物旅行和轻松社交场合。', 'مجوهرات مريحة وعصرية مثالية for عطلات نهاية الأسبوع غير الرسمية، مواعيد brunch، رحلات التسوق، والمناسبات الاجتماعية المرتاحة.', 'casual jewelry,relaxed,weekend,informal,comfortable', '休闲珠宝,轻松,周末,非正式,舒适', 'مجوهرات غير رسمية,مرتاح,نهاية الأسبوع,غير form,مريح', 1, 1, '2025-11-19 08:12:10', '2025-12-03 15:50:42');
INSERT INTO `product_categorytag` VALUES ('A00050008', 'special_occasion', 'Special Occasion', '特殊场合', 'مناسبة خاصة', 'Memorable and exquisite jewelry for important life events, anniversaries, milestones, and once-in-a-lifetime celebrations.', '用于重要人生事件、纪念日、里程碑和一生一次庆典的难忘精致珠宝。', 'مجوهرات لا تنسى ورائعة for الأحداث الحياتية المهمة， الذكرى السنوية، المحطات البارزة، والاحتفالات once-in-a-lifetime.', 'Memorable event jewelry', '难忘活动珠宝', 'مجوهرات مناسبة لا تنسى', '386780956460707840', '/api/files/PRODUCTEMO/2025/12/03/20251203152926061359.png', '/api/files/PRODUCTEMO/2025/12/03/20251203152926063349.jpg', '386780956473290752', '/api/files/PRODUCTEMO/2025/12/03/20251203152926066809.png', '386780956490067968', 5, 'A0005', '#9C27B0', 800, 1, 1, 1, 'Special Occasion Jewelry - Memorable Event Pieces', '特殊场合珠宝 - 难忘活动款式', 'مجوهرات مناسبة خاصة - قطع مناسبة لا تنسى', 'Exquisite jewelry for those special moments in life - anniversaries, graduations, promotions, and other significant milestones worth celebrating.', '精致珠宝为生活中的特殊时刻 - 纪念日、毕业、晋升和其他值得庆祝的重要里程碑。', 'مجوهرات رائعة for تلك اللحظات الخاصة in الحياة - الذكرى السنوية، التخرج， الترقيات، والمحطات البارزة الأخرى التي تستحق الاحتفال.', 'special occasion jewelry,anniversary,graduation,milestone,celebration', '特殊场合珠宝,纪念日,毕业,里程碑,庆典', 'مجوهرات مناسبة خاصة,ذكرى سنوية,تخرج,محطة بارزة,احتفال', 1, 1, '2025-11-19 08:12:10', '2025-12-03 15:50:42');
INSERT INTO `product_categorytag` VALUES ('A0006', 'craftsmanship', 'Craftsmanship', '工艺', 'الحرفية', 'The techniques and skills used in jewelry making, from traditional methods to innovative processes.', '用于珠宝制作的技术和技能，从传统方法到创新工艺。', 'التقنيات والمهارات المستخدمة in صناعة المجوهرات، from الطرق التقليدية to العمليات المبتكرة.', 'Jewelry making techniques', '首饰制作技艺', 'تقنيات صناعة المجوهرات', '386781061737738240', '/api/files/PRODUCTEMO/2025/12/03/20251203152951158551.jpg', '/api/files/PRODUCTEMO/2025/12/03/20251203152951164782.png', '386781061754515456', '/api/files/PRODUCTEMO/2025/12/03/20251203152951167868.png', '386781061762904064', 6, '0', '#8D6E63', 600, 1, 1, 0, 'Jewelry Craftsmanship - Handmade, Precision', '珠宝工艺 - 手工制作、精密工艺', 'حرفية المجوهرات - صناعة يدوية، دقة', 'Appreciate fine jewelry craftsmanship: handmade details, precision setting techniques.', '欣赏精美珠宝工艺：手工细节、精密镶嵌技术。', 'قدّر حرفية المجوهرات الدقيقة: تفاصيل صناعة يدوية، تقنيات تركيب دقيقة.', NULL, NULL, NULL, 1, 1, '2025-11-18 23:29:45', '2025-12-03 15:29:51');
