INSERT INTO `product_categorytag` VALUES ('A00060002', 'carving', 'Carving', '雕刻工艺', 'نحت', 'The art of shaping and detailing materials by removing portions to create three-dimensional designs and textures.', '通过去除部分材料来塑造和细化材料，创造三维设计和纹理的艺术。', 'فن تشكيل وتفصيل المواد by إزالة أجزاء to إنشاء تصاميم وأنسجة ثلاثية الأبعاد.', 'Material shaping art', '材料塑造艺术', 'فن تشكيل المواد', '386781061737738240', '/api/files/PRODUCTEMO/2025/12/03/20251203152951158551.jpg', '/api/files/PRODUCTEMO/2025/12/03/20251203152951164782.png', '386781061754515456', '/api/files/PRODUCTEMO/2025/12/03/20251203152951167868.png', '386781061762904064', 6, 'A0006', '#8D6E63', 200, 1, 1, 1, 'Carving Craftsmanship - Material Shaping Art', '雕刻工艺 - 材料塑造艺术', 'حرفية النحت - فن تشكيل المواد', 'Jewelry featuring detailed carving work that creates depth, texture, and intricate three-dimensional designs.', '采用精细雕刻工艺的珠宝，创造深度、纹理和复杂的三维设计。', 'مجوهرات what تتميز بأعمال نحت مفصلة what تخلق العمق، الملمس، وتصاميم ثلاثية الأبعاد معقدة.', 'carving craftsmanship,sculpting,3D designs,texture creation', '雕刻工艺,雕塑,3D设计,纹理创作', 'حرفية نحت,نحت,تصاميم ثلاثية الأبعاد,إنشاء ملمس', 1, 1, '2025-11-19 08:14:55', '2025-12-03 15:50:52');
