INSERT INTO `product_categorytag` VALUES ('A00060004', 'casting', 'Casting', '铸造工艺', 'صب', 'A manufacturing process where molten metal is poured into a mold to create specific shapes and designs.', '一种制造过程，将熔融金属倒入模具中以创造特定形状和设计。', 'عملية تصنيع where المعدن المنصهر is يصب in قالب to إنشاء أشكال وتصاميم محددة.', 'Mold shaping technique', '模具成型技术', 'تقنية تشكيل القالب', '386781061737738240', '/api/files/PRODUCTEMO/2025/12/03/20251203152951158551.jpg', '/api/files/PRODUCTEMO/2025/12/03/20251203152951164782.png', '386781061754515456', '/api/files/PRODUCTEMO/2025/12/03/20251203152951167868.png', '386781061762904064', 6, 'A0006', '#78909C', 400, 1, 1, 1, 'Casting Craftsmanship - Mold Shaping Technique', '铸造工艺 - 模具成型技术', 'حرفية الصب - تقنية تشكيل القالب', 'Jewelry created through precision casting techniques that allow for complex shapes and detailed designs.', '通过精密铸造技术创造的珠宝，可以实现复杂的形状和精细的设计。', 'مجوهرات مخلوقة through تقنيات صب دقيقة what تسمح بأشكال معقدة وتصاميم مفصلة.', 'casting craftsmanship,mold making,metal pouring,shape formation', '铸造工艺,模具制作,金属浇注,形状形成', 'حرفية صب,صنع قوالب,صب معدن,تشكيل شكل', 1, 1, '2025-11-19 08:14:55', '2025-12-03 15:50:52');
